INSERT INTO `lxlp_children` VALUES ('15641', '1', '7', '0', '0,', '-1', '-1', '', '李浩', '', '', '', '', 'admin', 'http://v.youku.com/v_show/id_XMTU0MzI4MzkwNA==.html', '', '<p>\r\n	<span style=\"font-family:SimHei;font-size:14px;color:#9933E5;\"><img src=\"/puti/uploads/image/20160421/1461255596.jpg\" alt=\"\" /><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimHei;font-size:14px;color:#9933E5;\">我叫小豆豆、今年17岁了，我的家乡在美丽的浙江宁海，在我记事开始、爸爸妈妈就在为我操劳担心，也因为我身体健康的原因、给我们家庭带来了严重的负担、我希望我快快长大、能独立生活、能让爸爸妈妈放心。感谢关心我的人，感谢九龙寺让我可以有一个追梦的地方</span>\r\n</p>', '撒旦法', 'uploads/image/20160421/1461260613.jpg', '', '161', '3', '1460613462', 'false', '', '0', '0', '', '', '0', '0', '', '4', '11500', '11501', '11501.1', '浙江九龙寺爱心屋', '爱让我越来越自信，我有目标、我有梦想', '14', '1', '55', '<p>\r\n	<img src=\"/puti/uploads/image/20160421/1461254594.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimHei;font-size:16px;color:#009900;\"> 为了让更多的智残儿童得到社会上爱心帮助，九龙寺与奕生缘企业共同设立【九龙寺菩提爱心屋】同时培养他们技能，让他们少有所学，青有所能，老有所依。让更多企业及爱心人士参与，让爱心菩提事业健康可持发展。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimHei;font-size:16px;color:#009900;\"><img src=\"/puti/uploads/image/20160421/1461253880.jpg\" alt=\"\" /><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimHei;font-size:16px;color:#009900;\"> 宁海九龙寺创建于唐朝大历年间（778年）距今已有1237年历史，位于宁波市宁海县境内，与著名国画大师（潘天寿）先生的故居相邻。同时交通非常便捷，火车站、汽车站距离九龙寺约1.5公里，沈海高速宁海出口约5分钟路程。 </span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimHei;font-size:16px;color:#009900;\"><img src=\"/puti/uploads/image/20160421/1461254372.jpg\" alt=\"\" /><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimHei;font-size:16px;color:#009900;\"> 历史记载、“百丈大师”于公元779年从现在的湖镇云游至宁海，见到“九龙山”结茅于此，后历代多位高僧都到九龙山坐茅闭关其中就有（罗汉彻禅师、法常禅师、临济高僧、虚堂智愚禅师、日易谷宾禅师等.....）明代军事刘伯温云游至天台山（因浙江的几大山脉与天台山脉同属一支、与括苍山脉同时并举，而四明山脉属天台山山脉的延续和支脉）突见九龙山气象更新、紫气凝聚，叹为绝妙\r\n      自唐朝至民国及共和国至今，九龙禅寺遭受多次劫难，众多寺僧离散，现虽土木忧存，双涧照旧回澜，经努力亦仅存之数楹颓室，斜立于春夏秋冬之中。</span> \r\n</p>', '<h3>\r\n	<span style=\"font-size:18px;color:#E53333;font-family:SimHei;\">我希望快点长大、能独立生活、不要爸爸妈妈为我以后生活担心，我也能养活自己、也能为社会做贡献。</span> \r\n</h3>', '', null, null, null, null, null, null);
